class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int now = 0;
        int ans = 0;
        for(int i=0;i<n-1;++i){
            if(s[i] == '1'){
                ++ now;
                if(s[i+1] == '0') ans += now;
            }
        }
        return ans;
    }
};
