class Solution {
public:
    int maxOperations(string s) {
        int i;
		int N = s.size();
		int Result = 0;
		int Count = 0;
		int State = 0;

		for(i = 0; i < N; i++)
		{
			if(s[i] == '1')
			{
				State = 0;
				Count++;
			}
			else if(State == 0)
			{
				State = 1;
				Result += Count;
			}
		}

		return Result;
    }
};
