class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0 
        res = []
        st = ed = 0 
        n = len(s)
        while st < n:
            while st < n and s[st] == '0':
                st += 1
            if st == n:
                break
            ed = st 
            while ed < n and s[ed] == '1':
                ed += 1
            res.append([st, ed - 1])
            st = ed + 1
        i = 0 
        l = len(res)
        ss = 0 
        for i in range(l):
            ss += res[i][1] - res[i][0] + 1
            ed = res[i][1]
            # print(ed, ss)
            if ed + 1 < n and s[ed + 1] == '0':
                ans += ss 
        return ans
