class Solution {
    public int maxOperations(String s) {
        int ops = 0;

        int prev = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '0') {
                if (i == s.length() - 1 || s.charAt(i + 1) == '1') {
                    ops += prev;
                }
            } else {
                prev++;
            }
        }

        return ops;
    }
}
