class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int count=0;
        int flag=0;
        for(int i=0;i<n;i++){
            char c=s[i];
            if(c=='0'){
                if(flag==0)continue;
                else{
                    ans+=count;
                    flag=0;
                }
            }
            else{
                if(flag==0){
                    count++;
                    flag=1;
                }
                else{
                    count++;
                }
            }
        }
        return ans;
    }
};
