class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int cnt1=0;
        char last='a';
        for(auto e:s) {
            if(e=='0'&&last!='0'&&cnt1>0) {
                res+=cnt1;
            }
            if(e=='1') {
                cnt1++;
            }
            last = e;
        }
        return res;
    }
};
