class Solution {
public:
    int maxOperations(string s) {
      int n = s.size();
      vector <int> dp (n), next (n, n);
      for (int i = n - 1; i >= 0; i--) {
        if (i + 1 < n) {
          if (s[i + 1] == '0') next[i] = next[i + 1];
          else next[i] = i + 1;
        }
      }

      int ans = 0;
      for (int i = n - 1; i >= 0; i--) {
        if (s[i] == '1') {
          if (i + 1 < n && s[i + 1] == '0') dp[i] += 1;
          if (next[i] < n) dp[i] += dp[next[i]];
        }
        ans += dp[i];
      }
      return ans;
    }
};
