class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        cnt = 0
        left = 0
        while left < len(s) and s[left] == '0':
            left = left + 1
        cnt = 1
        while left < len(s) - 1:
            if s[left + 1] == '1':
                left = left + 1
                cnt = cnt + 1
                continue
            res = res + cnt
            left = left + 1
            while left < len(s) and s[left] == '0':
                    left = left + 1
            cnt = cnt + 1
        return res
