class Solution 
{
public:
    int maxOperations(string s) 
    {
        long long ret = 0;
        int prev = 0, n = s.size();
        for(int i = 0; i < n; i++)
        {
            if(s[i] == '1') prev++;
            else if(i == n - 1 || s[i + 1] == '1')
            {
                ret += prev;
            }
        }
        return ret;
    }
};
