class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        one = 0
        zero = 0 
        for c in s:
            if c == '1':
                if zero > 0:
                    ans += one
                    zero = 0 
                    one += 1
                else:
                    one += 1
            else:
                zero += 1
        if zero > 0:
            ans += one
        return ans
