class Solution {
public:
    int maxOperations(string s) {
        int pre = 0,cur = 0;
        int ans = 0;
        int n  = s.size();
        for(int i= 0;i < s.size();i++){
            if(s[i] == '0' && i != s.size()-1)continue;
            ans+=pre;
            cur++;
            while(i + 1 < n && s[i + 1] == '1'){
                i++;
                cur++;
            }
            pre = cur;
        }
        return ans;
    }
};
