/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let res = 0;
    let cnt = 0;
    let pref = false;
    for (const c of s) {
        if (c === '1') {
            if (pref) {
                res += cnt;
            }
            cnt += 1;
            pref = false;
        } else {
            pref = true;
        }
    }
    if (s[s.length - 1] === '0') {
        res += cnt;
    }
    return res;
};
