class Solution {
    public int maxOperations(String s) {
        char[] cs = s.toCharArray();
        int n = cs.length;
        int rst = 0;
        int cnt = 0;
        int flag = 0;
        for (int i = 0; i < n; i ++) {
            if (cs[i] == '1') {
                if (flag == 1) {
                    flag = 0;
                    rst += cnt;
                }
                cnt ++;
            } else {
                flag = 1;
            }
        }
        if (flag == 1) rst += cnt;
        return rst;
    }
}
