class Solution {
public:
    int maxOperations(string s) {
        long long ans=0,cur=0;
        int flag=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cur++;
                flag=0;
            }
            else{
                if(!flag)ans+=cur;
                flag=1;

            }
        }
        return ans;
    }
};
