class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, ones = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1')
                ones++;
            else {
                ans += ones;
                while (i + 1 < s.size() && s[i + 1] == '0')
                    i++;
            }
        }

        return ans;
    }
};
