class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = s[0] - '0';
        for (int i = 1; i < s.size(); i++) {
            if (s[i - 1] == '1' && s[i] == '0') {
                // Start a gap
                ans += ones;
            }
            if (s[i] == '1') ones++;
        }
        return ans;
    }
};
