class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0,sum = 0;
        s +='0';
        int l = -1,r=-1;
        for(int i = 0;i<=n;i++){
            if(s[i]=='1'&&l==-1){l=i;r=i;}
            else if(s[i]=='1')r=i;
            else if(l!=-1&&r!=n-1){
                sum +=r-l+1;
                ans+=sum;
                //cout<<sum<<' '<<ans<<endl;
                l=-1;
            }
        }
        return ans;
    }
};
