class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        s=list(s)
        n=len(s)
        cnt=0
        total=0
        for index,i in enumerate(s):
            if i=='1':
                cnt+=1
                total+=1
            else:
                if index>0 and s[index-1]=='1':
                    ans=ans+total
                cnt=0
            # print(ans)
        return ans
