class Solution {
public:
    int maxOperations(string s) {
        vector<int> sez;
        for (int l=0; l<(int)s.size(); ) {
            int r = l;
            while (r < (int)s.size() && s[r] == s[l]) r++;
            if (s[l] == '1') sez.push_back(r - l);
            l = r;
        }

        for (int i=1; i<(int)sez.size(); i++) sez[i] += sez[i-1];

        int ans = 0;
        for (int i=0; i+1<(int)sez.size(); i++) ans += sez[i];
        if (s.back() == '0' && !sez.empty()) ans += sez.back();

        return ans;
    }
};
