class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        bool st=0;
        int ans=0;
        int ant=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                ant++;
            }else{
                int j=i;
                while(s[j]=='0'&&j<n) j++;
                i=j-1;
                ans+=ant;
            }
        }
        return ans;
    }
};
