class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int ones = 0;
        int i = 0;
        while (i < s.length()){
            if (s.charAt(i) == '1'){
                ones++;
                i++;
            }
            else{
                while (i < s.length() && s.charAt(i) == '0'){
                    i++;
                }
                ans += ones;
            }
            System.out.println(ans + " " + ones + " " + i);
        }
        return ans;
    }
}
