class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0, cnt = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (i != n - 1 && s[i] == '1' && s[i + 1] == '0') {
                ++cnt;
                ans += cnt;
            } else if (i != n - 1 && s[i] == '1' && s[i + 1] == '1') {
                ans += cnt;
            }
        }
        return ans;
    }
};
