class Solution {
    public int maxOperations(String s) {
        int total = 0;
        int zeros = 0;
        boolean inside = false;
        for(int i = s.length()-1; i>=0; i--) {
            if(s.charAt(i)=='1') {
                total = total + zeros;
                inside = false;
            } else {
                if(!inside) {
                    zeros++;
                    inside = true;
                }
            }
        }
        return total;
    }
}
