class Solution {
public:
    int maxOperations(string s) {

        int n = s.length();

        int z = 0,ans=0 , c = 0;

        for(int i = n-1 ; i >=0 ;  i--){

            if(s[i]=='0' && c==0){
                z++;
                c=1;}
            else if(s[i]=='0' && c==1){
                continue;

            }
            else{

                ans += z;
                c=0;

            }

        }

        return ans;

    }
};
