class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        int idx = 0;
        while (idx<s.length()){
            if (s[idx]=='1'){
                cnt ++;
                idx ++;
            }else{
                int nextIdx = idx;
                while (nextIdx<s.length()&&s[nextIdx]=='0'){
                    nextIdx ++;
                }
                idx = nextIdx;
                ans += cnt;
            }
        }
        return ans;
    }
};
