class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ones = 0;
        int zeros = 0;
        int prev = -1;
        int count = 0;

        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                if (prev == 0) {
                    count += ones;
                }
                ones++;
                prev = 1;
            }
            else {
                zeros++;
                prev = 0;
            }
        }

        if (prev == 0)
            count += ones;

        return count;
    }
};
