class Solution:
    def maxOperations(self, s: str) -> int:
        count1s = 0
        i = 0
        n = len(s)
        sol = 0
        while i < n:
            if s[i] == '1':
                count1s += 1  
                i += 1  
            else:
                while i < n and s[i] == '0':
                    i += 1
                sol += count1s

        return sol
