class Solution {
public:
    int maxOperations(string s) {
        int countOne = 0;
        int result = 0;
        bool counted = true;
        for (char c: s) {
            if (c == '1') {
                countOne++;
                counted = false;
            } else if (!counted) {
                result += countOne;
                counted = true;
            }
        }
        return result;
    }
};
