class Solution:
    def maxOperations(self, s: str) -> int:
        # 尽量从左边的1开始操作
        ones = []  # 连续1的个数
        temp = 0
        for ch in s + '0':
            if ch == '1':
                temp += 1
            elif temp:
                ones.append(temp)
                temp = 0

        ans = sum(accumulate(ones))  # ones的前缀和之和
        # 如果s以1结尾，则不能加上最后ones的总和
        if s.endswith('1'):
            ans -= sum(ones)
        return ans
