class Solution {
    public int maxOperations(String s) {
        int stackSize = 0;
        int result = 0;
        boolean hitZero = false;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                if (hitZero) {
                    result += stackSize;
                }

                stackSize++;
                hitZero = false;
            } else {
                hitZero = true;
            }
        }

        if (hitZero) {
            result += stackSize;
        }

        return result;
    }
}
