class Solution {
    public int maxOperations(String s) {
        int c = 0;int ans = 0;boolean h = false;
        s = s+"1";
        for(int i=0;i<s.length();++i) {
            if(s.charAt(i) == '0') {
                if(h) {ans+=c;h=false;}
                continue;
            }
            else {
                h= true;
                c++;
            }
        }
        return ans;
    }
}
