function maxOperations(s: string): number {
    let moves = 0;
    let ones = 0;

    for(let i = 0; i < s.length; i++) {
        if (s[i] === '1') {
            ones++;
        } 
        if ((s[i] === '0') && ((s[i + 1] ?? '1') === '1')) {
            moves += ones    
        }
    }

    return moves
};
