class Solution {
public:
    int maxOperations(string s) 
    {
        vector<int> pref(s.size(),0);
        int x=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                x++;
            }
            pref[i]=x;
        }

        int ans=0;
        for(int i=0;i<s.size();i++)
        {
            if(i!=0 && s[i]=='0' && s[i-1]!='0')
            {
                ans+=pref[i];
            }
        }
        return ans;
    }
};
