class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int ans=0;
        for(int i=s.length()-1;i>=0;){
            if(s[i]=='0'){
                count++;
                while(i>=0 && s[i]=='0'){
                    i--;
                }
            }
            else{
                ans+=count;
                i--;
            }
        }
        return ans;
    }
};
