class Solution {
public:
    int maxOperations(string s) 
    {
        int n = s.size();

        int ans = 0;
        int cnt = 0;
        for(int i = 0; i < n; i++)
        {
            if(s[i] == '1')
            {
                int j = i;
                while(j < n && s[j] == '1') j++;
                cnt += (j - i);
                i = j - 1;
            }
            else
            {
                int j = i;
                while(j < n && s[j] == '0') j++;
                ans += cnt;
                i = j - 1;
            }
        }

        // cout << "cnt is " << cnt << endl;
        // if(s[n - 1] != '1') ans += cnt;
        return ans;
    }
};
