class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, ones = 0;
        bool zero = false;
        s += "1";
        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '0') {
                zero = true;
            }
            if (s[i] == '1') {
                if (zero) {
                    zero = false;
                    ans += ones;
                }
                ++ones;
            }
        }
        return ans;
    }
};
