class Solution {
public:
    int maxOperations(string s) {
        int zero_count = 0;
        int one_num = 0;
        int res = 0;

        for(auto &it : s) {
            if(it == '1') {
                if(zero_count) {
                    res += one_num;
                    zero_count = 0;
                }
                ++one_num;
            }
            else ++zero_count;
        }
        if(zero_count) {
            res += one_num;
            zero_count = 0;
        }

        return res;
    }
};
