class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int a=0,b=0;
        for(int i=0;i<(int)s.size()-1;i++){
            if(s[i]=='1')a++;
            if(s[i]=='1'&&s[i+1]=='0'){
                res+=a;
            }
        }
        return res;
    }
};
