class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int k = n - 1;
        while(k >= 0 && s[k] == '1') k--;
        int cnt1 = 0, cnt = 0;
        bool added = true;
        for(int i = 0; i <= k; ++i) {
            if(s[i] == '0') {
                if(cnt1 > 0 && !added) {
                    cnt += cnt1;
                    added = true;
                }
            } else {
                cnt1 += 1;
                added = false;
            }
        }
        return cnt;
    }
};
