class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0;
        int n = (int)s.size();
        int i = 0;
        for( ; i < n ; ) {
            if(s[i] == '1') {
                ones++;
                i++;
                if(i < n && s[i] == '1') {
                    continue;
                } else {
                  if(i < n && s[i] == '0') {
                   ans += ones;
                  }
                  while(i < n && s[i] == '0') {
                    i++;
                  }
                }
            } else {
                ans += ones;
                while(i < n && s[i] == '0') {
                    i++;
                }
            }
        }
        return ans;
    }
};
