class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), count = 0 , ans = 0;
        int i = n-1;
        while(i>=0 && s[i] == '1')i--;

        if(i<0)return 0;

        for(;i>=0 ; i--){

             if(s[i] == '1')ans += count;

             if(i>0 && s[i] == '0' && s[i-1] == '1') count++;


        }
        return ans;
    }
};
