class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        last = 0
        s = list(s)
        s.reverse()
        i = 1
        while i < len(s):
            if s[i] == "0":
                i += 1
                continue
            if s[i - 1] == "0":
                last += 1
                res += last
            else:
                res += last
            i += 1
        return res
