class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        vector<int> v;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') v.push_back(i);
        }
        v.push_back(s.size());
        for(int i = 1; i < v.size(); i++) {
            if(v[i] - v[i - 1] > 1) ans += i;
        }
        return ans;
    }
};
