class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int ans = 0, cnt = 0, n = s.size();
        int i = n - 1;
        while (i >= 0) {
            if (s[i] == '0') i--;
            else {
                int k = 0;
                while (i - k >= 0 && s[i - k] == '1') k++;
                ans += k*cnt++;
                i -= k;
            }
        }
        return ans;
    }
};
