class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        char pre = s[0];
        int one_count = 0;
        if (pre == '1') {
            one_count++;
        }

        for (int i = 1; i < s.size(); i++) {
            if (s[i] == '1') {
                one_count++;
            } else if (pre == '1') {
                res += one_count;
            }
            pre = s[i];
        }

        return res;
    }
};
