class Solution:
    def maxOperations(self, s: str) -> int:

        ones = 0
        moves = 0
        wasZero = False

        for i in range(len(s)):
            if s[i] == "1":
                wasZero = False
                ones += 1
                continue

            if s[i] == "0" and not wasZero:
                moves += ones
                wasZero = True

        return moves
