class Solution {
public:
  int maxOperations(string s) {
    bool moved = false;
    int ans = 0;
    int cnt = 0;
    for (auto e: s) {
      if (e == '0') {
        if (!moved) {
          moved = true;
          ans += cnt;
        }
      }
      else {
        cnt ++;
        moved = false;
      }
    }
    return ans;
  }
};
