class Solution {
    public int maxOperations(String s) {
        int curr = 0;

        s += "1";
        int n = s.length();
        int i =0;
        int res = 0;
        while(i < n){
            if(s.charAt(i) == '1') curr++;
            int zero = 0;
            while(i < n && s.charAt(i) == '0'){
                zero++;
                i++;
            }
            res += (zero > 0 ) ? curr : 0;
            if(zero == 0) i++;
        }

        return res;
    }
}
