public class Solution {
    public int MaxOperations(string s) {
        int count1=0;
        int ans=0;
        bool isFirst0=true;
        for(int i =0;i<s.Length;i++)
        {
            if(s[i]=='1')
            {
                count1++;
                isFirst0=true;
            }
            else
            {
                if(isFirst0) 
                {
                    ans+=count1;
                    isFirst0=false;
                }
            }
        }
        return ans;
    }
}
