class Solution {
public:
    int maxOperations(string s) {
        int ctr=0;
        int last=0;
        int ans=0;
        int tot=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                if(last&&i-1>=0&&s[i-1]!='1')
                {
                    ans+=last;
                }
                ctr++;
            }
            else{
                if(ctr!=0)
                last=last+ctr;
                ctr=0;

            }

        }
        if(s.back()=='0')
        {
            ans+=last;
        }
        return ans;

    }
};
